import React, { Component } from 'react'
import PropTypes from 'prop-types'; 

export class TodoItem extends Component {
    setStyle = () => {
        return {
            backgroundColor: this.props.todo.completed ? '#ccc' : 'red',
            textDecoration: !this.props.todo.completed ? 'line-through' : 'none',
            padding: '5px',
            borderBottom: '1px #000 dotted'
        }
    }

    isBoxChecked = () => {
        return this.props.todo.completed ? false :true
    }



    render() {

        const {id, title} = this.props.todo;

        return (
            <div>
                <p className="row text-left">
                    <span className='col-sm-8'  style = {this.setStyle()}>
                        <input type="checkbox" checked={this.isBoxChecked()} onChange={this.props.markCompleted.bind(this,id)}/>
                        {title}
                    </span>
                    <span className='col-sm-4'>
                        <span className='btn-group'>
                        <button type="button" className="btn btn-warning">Edit</button>
                        {" "}
                        <button type="button" className="btn btn-danger">Delete</button>
                        </span>
                    </span>

                </p>
            </div>
        )
    }
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem
