import React, { Component } from 'react'
import PropTypes from 'prop-types'; 
// import TodoItem from './css/TodoItem.css'

export class TodoItem extends Component {
    setStyle = () => {
        return {
            backgroundColor: !this.props.todo.completed ? '#ccc' : 'orange',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none',
            padding: '5px',
            borderBottom: '1px #000 dotted'
        }
    }

    isBoxChecked = () => {
        return !this.props.todo.completed ? false :true
    }

    buttonChange = () => {
        console.log("Echo " + document.getElementsByClassName("btn-warning").value)
    }

    componentDidMount() {
        window.addEventListener('resize', () => {
            this.setState({
                isMobile: window.innerWidth < 1200
            });
        }, false);
        console.log('hey' + this.setState())
    }


    render() {

        const {id, title} = this.props.todo;
        const className = this.componentDidMount();

        return (
            <div className="container">
                <div className="row pb-2">
                    <div className='col-8 col-md-10 text-left'  style = {this.setStyle()}>
                        <input type="checkbox" checked={this.isBoxChecked()} onChange={this.props.markCompleted.bind(this,id)}/>
                        {" "}{title}
                    </div>
                    <div className='col-4 col-md-2 text-right'>
                        <div className='btn-group'>
                            <p>{className}</p>
                        <button 
                            onClick = {this.buttonChange()}
                            type="button" 
                            className="btn btn-warning edit"
                            onClick={this.props.editItem.bind(this,id)} 
                        >Edit</button>
                        <button 
                            type="button" 
                            className="btn btn-danger delete"
                            onClick={this.props.deleteItem.bind(this,id)}
                        >Delete</button>
                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem
