import React, { Component } from 'react'
import PropTypes from 'prop-types'; 

export class TodoItem extends Component {
    setStyle = () => {
        return {
            backgroundColor: this.props.todo.completed ? '#ccc' : 'red',
            textDecoration: !this.props.todo.completed ? 'line-through' : 'none',
            padding: '5px',
            borderBottom: '1px #000 dotted'
        }
    }
    render() {
        const {id, title} = this.props.todo;
        isChecked = () => {
            this.props.todo.completed ? true : false
        }
        return (
            <div style = {this.setStyle()}>
                <p>
                    <input type="checkbox" checked={isChecked()} onChange={this.props.markCompleted.bind(this,id)}/>
                    {title}
                </p>
            </div>
        )
    }
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem
