import React, { Component } from 'react'
import PropTypes from 'prop-types'; 

export class TodoItem extends Component {
    setStyle = () => {
        return {
            backgroundColor: !this.props.todo.completed ? '#ccc' : 'orange',
            textDecoration: this.props.todo.completed ? 'line-through' : 'none',
            padding: '5px',
            borderBottom: '1px #000 dotted'
        }
    }

    isBoxChecked = () => {
        return !this.props.todo.completed ? false :true
    }



    render() {

        const {id, title} = this.props.todo;

        return (
            <div className="container">
                <p className="row">
                    <div className='col-8 col-md-10 text-left'  style = {this.setStyle()}>
                        <input type="checkbox" checked={this.isBoxChecked()} onChange={this.props.markCompleted.bind(this,id)}/>
                        {" "}{title}
                    </div>
                    <div className='col-4 col-md-2 text-right'>
                        <div className='btn-group'>
                        <button 
                            type="button" 
                            className="btn btn-warning"
                            onClick={this.props.editItem.bind(this,id)}
                        >x</button>
                        <button 
                            type="button" 
                            className="btn btn-danger"
                            onClick={this.props.deleteItem.bind(this,id)}
                        >x</button>
                        </div>
                    </div>

                </p>
            </div>
        )
    }
}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired
}

export default TodoItem
